package Strings;

public class concatinate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="well";
		String s2="come";
		String s3="to";
		String s4="Java";
		System.out.println(s1+" "+s2+" "+s3+" "+s4);
		System.out.println(s1.concat(" "+s2).concat(" "+s3).concat(" "+s4));
		

	}

}
